
import { ADD_ITEM, SUBTRACT_ITEM, ADD_TO_CART, SUBTRACT_QUANTITY } from "../action-types/cart-actions";
import { addItem, subtractItem, addToCart, subtractQuantity } from "../cartActions";

const initState = {
    items: [
        {
            id: 1,
            name: 'Paneer Tikka',
            img: 'https://www.indianveggiedelight.com/wp-content/uploads/2021/08/air-fryer-paneer-tikka-featured.jpg',
            price: 250,
            quantity: 0
        },
        {
            id: 2,
            name: 'Chili Chicken',
            img: 'https://static.toiimg.com/photo/53094926.cms',
            price: 370,
            quantity: 0
        },
        {
            id: 3,
            name: 'Alfredo Pasta',
            img: 'data:image/jpeg;base64,',
            price: 480,
            quantity: 0
        },
        {
            id: 4,
            name: 'Cold Lasagne',
            img: 'data:image/jpeg;base64,',
            price: 750,
            quantity: 0
        },
        {
            id: 5,
            name: 'Pizza Margherita',
            img: 'https://images.crateandbarrel.com/is/image/Crate/frame-margherita-pizza-1',
            price: 370,
            quantity: 0
        },
    ],
    addedItems: [],
    total: 0
}

const cartReducer = (state = initState, action) => {

    if(action.type === ADD_ITEM){
      let addedItem = state.items.find(item=> item.id === action.id)
          addedItem.quantity = addedItem.quantity + 1 
          let newTotal = state.total + addedItem.price
          return{
              ...state,
              quantity: addedItem.quantity,
              total: newTotal
          }
    }

    if(action.type === SUBTRACT_ITEM){
        let addedItem = state.items.find(item=> item.id === action.id)
        if(addedItem.quantity>0){
          addedItem.quantity = addedItem.quantity - 1
          let newTotal = state.total - addedItem.price
          return{
            ...state,
            quantity: addedItem.quantity,
            total: newTotal
        }
        }
        else{
          return{
            ...state,
            quantity: addedItem.quantity
        }
        }

    }

    if(action.type === SUBTRACT_QUANTITY){
        let addedItem = state.items.find(item=> item.id === action.id)
        if(addedItem.quantity>1){
          addedItem.quantity = addedItem.quantity - 1
          let newTotal = state.total - addedItem.price
          return{
            ...state,
            quantity: addedItem.quantity,
            total: newTotal
         }
        }

        else if(addedItem.quantity===1){
            let itemToRemove= state.addedItems.find(item=> action.id === item.id)
            let new_items = state.addedItems.filter(item=> action.id !== item.id)

            let newTotal = state.total - itemToRemove.price
            return{
                ...state,
                addedItems: new_items,
                total: newTotal
            }
        }
    }

    if(action.type === ADD_TO_CART){
        let addedItemsCopy = state.items.filter(item=> item.quantity !== 0)
        return{
            ...state,
            addedItems: addedItemsCopy
        }
    }

      return state
}

export default cartReducer